const spans=document.getElementsByTagName('span');let hue=0;function updateColor(){for(let i=0;i<spans.length;i++){if(spans[i].classList.contains('rainbow')){spans[i].style.color=`hsl(${hue}, 100%, 50%)`}else if(spans[i].classList.contains('rainglow')){spans[i].style.textShadow=`0 0 10px hsl(${hue}, 100%, 50%), 0 0 20px hsl(${hue}, 100%, 50%), 0 0 30px hsl(${hue}, 100%, 50%), 0 0 40px hsl(${hue}, 100%, 50%)`}}
hue=(hue+1)%360}
setInterval(updateColor,10)